rule.definition:
=a!localVariables(
  local!items: {
    {item: "Item 1", qty: 1, unitPrice: 10},
    {item: "Item 2", qty: 2, unitPrice: 20},
    {item: "Item 3", qty: 3, unitPrice: 30},
    {item: "Item 4", qty: 4, unitPrice: 40},
    {item: "Item 5", qty: 5, unitPrice: 50},
    {item: "Item 6", qty: 6, unitPrice: 60},
    {item: "Item 7", qty: 7, unitPrice: 70},
    {item: "Item 8", qty: 8, unitPrice: 80},
    {item: "Item 9", qty: 9, unitPrice: 90},
    {item: "Item 10", qty: 10, unitPrice: 100}
  },
  #"SYSTEM_SYSRULES_gridLayout"(
    label: "Products",
    instructions: "Update the item name, quantity, or unit price.",
    headerCells: {
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Item"),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Qty"),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Unit Price"),
      #"SYSTEM_SYSRULES_gridLayoutHeaderCell"(label: "Total", align: "RIGHT")
    },
    columnConfigs: {
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE", weight: 5),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE"),
      #"SYSTEM_SYSRULES_gridLayoutColumnConfig"(width: "DISTRIBUTE", weight: 2)
    },
    rows: #"SYSTEM_SYSRULES_forEach"(
      items: local!items,
      expression: #"SYSTEM_SYSRULES_gridRowLayout"(
        contents: {
          #"SYSTEM_SYSRULES_textField"(
            value: fv!item.item,
            saveInto: fv!item.item
          ),
          #"SYSTEM_SYSRULES_integerField"(
            value: fv!item.qty,
            saveInto: fv!item.qty
          ),
          #"SYSTEM_SYSRULES_floatingPointField"(
            value: fv!item.unitPrice,
            saveInto: fv!item.unitPrice
          ),
          #"SYSTEM_SYSRULES_textField"(
            value: dollar(tointeger(fv!item.qty) * todecimal(fv!item.unitPrice)),
            readOnly: true,
            align: "RIGHT"
          )
        }
      )  
    ),
    height: "SHORT",
    rowHeader: 1
  )
)
